import React from "react";
import ChessPiece from "../ChessPiece/ChessPiece";
import './ChessTable.css';

function ChessTable(props) {
  const tableRender = () => {
    const letras = ["a", "b", "c", "d", "e", "f", "g", "h"];
    const tableCells = [];

    for (let i = 0; i <= 7; i++) {
      for (let l = 0; l <= 7; l++) {
        let color = 'color0';
        if((i + l) % 2 === 1){
            color = 'color1';
        }
        const tableCell = {
          id: i + l,
          position: `${letras[l]}${8 - (i + 1)}`,
          color: color,
        };
        tableCells.push(tableCell);
      }
    }
    return tableCells;
  };

  const tableCells = tableRender();

  return (
    <div className="chessTable">
      {tableCells.map((tableCell, index) => {
        return (
          <div
            key={index}
            id={tableCell.id}
            className={`chessTable__cell ${tableCell.color}`}
          >
              <ChessPiece gameState={props.gameState} cellId={index}/>
          </div>
        );
      })}
    </div>
  );
}

export default ChessTable;
