import React from 'react';

function ChessTable(){
    
    const tableRender = () => {
        const letras = [a, b, c, d, e, f, g, h];
        const tableCells = [];

        for(let i = 0; i <= 7; i++){
            for(let l = 0; l <= 7; l++){
                const tableCell = {
                    id: i+k+1,
                    position: `${letras[l]}${8-(i+1)}`,
                    color:(i+k)%2
                };
                tableCells.push(tableCell);
            }         
        }
        return tableCells;
    }

    const tableCells = tableRender();

    return (
        <div className = 'chessTable'>
            {tableCells.map((tableCell, index) => {
                return(
                    <div key={index} id={tableCell.id} className={'chessTable__cell'+' '+tableCell.color} ></div>
                );
            })}
        </div>
    );
}

export default ChessTable;
